import React from 'react';

/**
 * Simple component with no state.
 *
 * See the basic-react from lecture 11 for an example of adding and
 * reacting to changes in state.
 */
const oneDay = 60 * 60 * 24 * 1000;
const todayTimestamp = Date.now() - (Date.now() % oneDay) +
(new Date().getTimezoneOffset() * 1000 * 60);

/**
  *
  */

class Picker extends React.Component {
  /**
  *
  */

    state = {
      getMonthDetails: [],
    }
    /**
  *
  */
    componentDidMount() {
      window.addEventListener('click', this.addBackDrop);
    }
    /**
  *
  */
    componentWillUnmount() {
      window.removeEventListener('click', this.addBackDrop);
    }
   /**
    * @param e parameter
    */
    addBackDrop = (e) => {
      if (state.showDatePicker &&
          !ReactDOM.findDOMNode().contains(e.target)) {
        showDatePicker(false);
      }
    }

    showDatePicker =(showDatePicker=true)=> {
      setState({showDatePicker});
    }

    render() {
      return (
        <div className='MyDatePicker'>
          <div className='mdp-input' onClick={()=>
            showDatePicker(true)}>
            <input type='date'/>
          </div>
          {this.state.showDatePicker ? (
              <div className='mdp-container'>
              </div>
                ) : ''}
        </div>
      );
    }
}

export default Picker;
