import React from 'react';
import {Card, CardTitle, CardText, Row, Col} from 'react';
import './MailItem.css';

const MailItem = (props) => {
  return (
    <Row className='MailItem'>
      <Col lg="12" className="w-100">
        <Card body onClick={props.clicked} className="Custom-Card">
          <CardTitle className="font-italic">{props.mailId}</CardTitle>
          <CardText onClick={props.clicked} className="font-weight-bold">
            Subject : {props.mailSubject}
          </CardText>
          <p className= "font-italic ml-auto">{props.mailDate}</p>
        </Card>
      </Col>
    </Row>
  );
};

export default MailItem;
