import React, {Component} from 'react';
import {Col} from 'react';
import {connect} from 'react';
import Wrapper from './Wrapper';
import MailItem from './MailItem';
import * as MailItemCreator from './MailCreator';
import Search from './Search';

class MailList extends Component {
  state = {
    searchtext: '',
  };

  searchChangedHandler = (e) => {
    this.setState({
      searchtext: e.target.value,
    });
    this.props.onSearch(this.state.searchtext);
  };

  cardClickedHandler = data => {
    this.props.onSendData(data);
  };
  deleteClickedHandler = data => {
    this.props.onDeleteData(data);
  };

  render() {
    return (
      <Wrapper>
        <Col sm="3">
          <Search changed={this.searchChangedHandler} />
          {this.props.data.map(data => {
            return (
              <MailItem
                mailId={data.from}
                mailSubject={data.subject}
                mailDate={data.sentDate}
                key={data.id}
                clicked={() => this.cardClickedHandler(data)}
                deleteClicked={() => this.deleteClickedHandler(data)}
              />
            );
          })}
        </Col>
        <Mails />
      </Wrapper>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSendData: data => dispatch(MailItemCreator.getMailData(data)),
    onDeleteData: data => dispatch(MailItemCreator.deleteMail(data)),
    onSearch: searchText => dispatch(MailItemCreator.search(searchText))
  };
};

export default connect(null, mapDispatchToProps)(MailList);
